mapper.select:
select  nvl(MAX(mid), 'N')
from members
where
 mid = #{info}
 mnickname = #{info}

select *
from members
where mid = #{mid} and mpw = #{mpw}

mapper.update:
UPDATE members
SET MNICKNAME = #{mnickname}, MPROFILE = #{mprofile}
WHERE mid = #{mid}

UPDATE members
SET mpw = #{mpw}
WHERE mid = #{mid}
